// frontend/src/components/CajerosTable.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CajerosTable = ({ token, rol }) => {
  const [cajeros, setCajeros] = useState([]);
  const [search, setSearch] = useState('');
  const [newCajero, setNewCajero] = useState({ username: '', password: '' });
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCajeros = async () => {
      try {
        const res = await axios.get(`http://localhost:8000/cajeros?search=${search}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCajeros(res.data);
      } catch (error) {
        console.error('Error al cargar cajeros');
      }
    };
    fetchCajeros();
  }, [token, search]);

  const handleAddCajero = async () => {
    try {
      await axios.post('http://localhost:8000/cajeros', newCajero, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Cajero creado exitosamente');
      setNewCajero({ username: '', password: '' });
      const res = await axios.get(`http://localhost:8000/cajeros?search=${search}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCajeros(res.data);
    } catch (error) {
      setError(error.response?.data?.detail || 'Error al crear cajero');
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl">Cajeros</h1>
      <input
        type="text"
        placeholder="Buscar cajero..."
        className="border p-2 m-2 w-full"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      {(rol === 'supervisor' || rol === 'director') && (
        <div className="p-4 max-w-md mx-auto">
          <h2 className="text-xl">Agregar Cajero</h2>
          {error && <p className="text-red-500">{error}</p>}
          <input
            type="text"
            placeholder="Nombre de usuario"
            className="border p-2 m-2 w-full"
            value={newCajero.username}
            onChange={(e) => setNewCajero({ ...newCajero, username: e.target.value })}
          />
          <input
            type="password"
            placeholder="ContraseÃ±a"
            className="border p-2 m-2 w-full"
            value={newCajero.password}
            onChange={(e) => setNewCajero({ ...newCajero, password: e.target.value })}
          />
          <button
            className="bg-blue-500 text-white p-2 m-2 w-full"
            onClick={handleAddCajero}
          >
            Agregar Cajero
          </button>
        </div>
      )}
      <table className="border w-full">
        <thead>
          <tr>
            <th className="border">ID</th>
            <th className="border">Nombre</th>
          </tr>
        </thead>
        <tbody>
          {cajeros.map(cajero => (
            <tr key={cajero.id}>
              <td className="border">{cajero.id}</td>
              <td className="border">{cajero.nombre}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CajerosTable;
