// frontend/src/components/ClientesTable.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ClientesTable = ({ token, rol }) => {
  const [clientes, setClientes] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        const res = await axios.get(`http://localhost:8000/clientes?search=${search}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setClientes(res.data);
      } catch (error) {
        console.error('Error al cargar clientes');
      }
    };
    fetchClientes();
  }, [token, search]);

  return (
    <div className="p-4">
      <h1 className="text-2xl">Clientes</h1>
      <input
        type="text"
        placeholder="Buscar cliente..."
        className="border p-2 m-2 w-full"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <table className="border w-full">
        <thead>
          <tr>
            <th className="border">ID</th>
            <th className="border">Nombre</th>
          </tr>
        </thead>
        <tbody>
          {clientes.map(cliente => (
            <tr key={cliente.id}>
              <td className="border">{cliente.id}</td>
              <td className="border">{cliente.nombre}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ClientesTable;
